        Scanner sc = new Scanner(System.in);
        int x = sc.nextInt();
        int i = 1;
        int t = (int)(Math.pow(x, 2));
        int n = x;
        
        while(n &gt;= 10)
        {
            n /= 10;
            i++;
        }

        if((t % (Math.pow(10,i))) == x)
