	public class CrashError extends Error {
		
		public CrashError() { 
			this(&quot;simulated crash&quot;);
		}
		
		public CrashError(String msg) {
			super(msg);
		}
		
		public static void doCrash() {
			throw new CrashError();
		}
		
	}


