To solve this problem you can use filter to check when last time session is used by requests other than your pooling and invalidate session manually

    import java.io.IOException;
    import java.util.Set;
    import javax.servlet.FilterChain;
    import javax.servlet.ServletException;
    import javax.servlet.http.HttpServletRequest;
    import javax.servlet.http.HttpServletResponse;
    import javax.servlet.http.HttpSession;
    import lombok.extern.slf4j.Slf4j;
    import org.springframework.beans.factory.annotation.Value;
    import org.springframework.core.annotation.Order;
    import org.springframework.stereotype.Component;
    import org.springframework.web.filter.OncePerRequestFilter;
    
    @Slf4j
    @Component
    @Order(Integer.MIN_VALUE)
    public class SessionTimeoutExcludeUrlsFilter extends OncePerRequestFilter {
      private final static String INCLUDED_LAST_ACCESS_TIME = &quot;includedLastAccessTime&quot;;
    
      private final Set&lt;String&gt; sessionTimeoutExcludeUrls;
    
      public SessionTimeoutExcludeUrlsFilter(@Value(&quot;${http.session.timeout.exclude-urls}&quot;) Set&lt;String&gt; sessionTimeoutExcludeUrls) {
        this.sessionTimeoutExcludeUrls = sessionTimeoutExcludeUrls;
      }
    
      @Override
      protected void doFilterInternal(HttpServletRequest httpRequest, HttpServletResponse httpResponse,
          FilterChain filterChain) throws ServletException, IOException {
        HttpSession session = httpRequest.getSession();
        if (!sessionTimeoutExcludeUrls.contains(httpRequest.getRequestURI())) {
          session.setAttribute(INCLUDED_LAST_ACCESS_TIME, System.currentTimeMillis());
        }
    
        if (session.getAttribute(INCLUDED_LAST_ACCESS_TIME) != null) {
