    private void checkCipherState() {
        if (!(this instanceof NullCipher)) {
            if (!initialized) {
                throw new IllegalStateException(&quot;Cipher not initialized&quot;);
            }
            if ((opmode != Cipher.ENCRYPT_MODE) &amp;&amp;
