    @Value(&quot;${extraction.api-spec.json}&quot;)
    String filename;
    @Value(&quot;${springdoc.api-docs.path}&quot;)
    String apiDocJsonPath;

    @Test
    void extractApiSpecJsonFile() throws Exception {
        File file = new File(filename);
