    package com.example.assignmentsurvey;
    
    import com.fasterxml.jackson.databind.ObjectMapper;
    
    import java.io.*;
    import java.lang.reflect.Field;
    import java.lang.reflect.Modifier;
    import java.util.*;
    
    
    
    public class CSVHandler &lt;T&gt;{
        private String delimiter = &quot;,&quot;;
        private String newline = &quot;\n&quot;;
        private String header;
    
        public CSVHandler(String header, String delimiter, String newline) {
            this.header = header;
            this.delimiter = delimiter;
            this.newline = newline;
        }
    
        public void writeCSV(List&lt;T&gt; data, String fileName, boolean append) {
            FileWriter fileWriter = null;
            try {
                File file = new File(fileName);
                fileWriter = new FileWriter(file, append);
                if (!append) {
                    fileWriter.append(header);
                    fileWriter.append(newline);
                }
                for (T element : data) {
                    // int rows = data.size();
                    Field[] fields = element.getClass().getDeclaredFields();
                    int discard = 0;
                    for (Field f : fields) {
                        // get rid of public var, which are shared variables
                        if (Modifier.isPublic(f.getModifiers())) {
                            discard += 1;
                        }
                    }
                    int columns = fields.length - discard;
                    int count = 0;
                    for (Field f : fields) {
                        // only list private variables internal to a class
                        if (Modifier.isPrivate(f.getModifiers())) {
                            count += 1;
                            f.setAccessible(true);
                            fileWriter.append(f.get(element).toString());
                            if (count % columns != 0) {
                                fileWriter.append(delimiter);
                            }
                        }
                    }
                    fileWriter.append(newline);
                }
            } catch (Exception e) {
                e.printStackTrace();
            } finally {
                try {
                    fileWriter.flush();
                    fileWriter.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        }
    
