                try {
                    String line = &quot;&quot;;
                    bReader = new BufferedReader(new FileReader(file));
                    bReader.readLine();
    
                    while ((line = bReader.readLine()) != null) {
                        T element = parser.parseLine(line.split(delimiter));
                        if (element != null)
                            data.add(element);
                    }
                } catch (Exception e) {
                    e.printStackTrace();
                } finally {
                    try {
                        if (bReader != null)
                            bReader.close();
                    } catch (IOException e) {
                        e.printStackTrace();
                    }
                }
            }
            return data;
