Try this code

    public static int mySqrt(int x) {
        long left = 0;
        long right = x;

        if(x &lt; 2){
            return x;
        }
        while(left &lt; right){
            long mid = left + ((right - left) / 2);

            if(mid * mid == x){
                return (int)mid;

            }
            else if(mid * mid &lt; x){
                left = mid + 1;
            }
            else{
                right = mid;
            }
        }
        return (int)(left - 1);
    }

