    	private static final String SOURCE_SCHEME = &quot;YourKeyPath&quot;;
    	private static final String TARGET_AGENCY = &quot;YOUR_TARGET_AGENCY&quot;;
    	private static final String TARGET_SCHEME = &quot;YourKeyPath&quot;;
    
    	public void setParameter(Map arg0) {
    		param = arg0;
