    			PGPSecretKeyRing keyRing = (PGPSecretKeyRing) rIt.next();
    			Iterator kIt = keyRing.getSecretKeys();
    			while (secretKey == null &amp;&amp; kIt.hasNext()) {
    				PGPSecretKey key = (PGPSecretKey) kIt.next();
    				if (key.isSigningKey()) {
    					secretKey = key;
    				}
    			}
    		}
    
