    		}
    
    		InputStream clear = pbe.getDataStream(new BcPublicKeyDataDecryptorFactory(sKey));
    
    		PGPObjectFactory plainFact = new PGPObjectFactory(clear);
    
    		Object message = plainFact.nextObject();
    
