                jsonWriter.value(instant.toEpochMilli());
            } else {
                jsonWriter.nullValue();
            }
        }
    
        @Override
        public Instant read(final JsonReader jsonReader) throws IOException {
            long millisEpoch = jsonReader.nextLong();
            if (millisEpoch == -1) {
                return null;
            } else {
