I solved the problem. This solution works:

    import java.util.*;
    
    public class FastestPath {
        public static void main(String[] args) {
            Scanner scanner = new Scanner(System.in);
            int N = scanner.nextInt(); // Number of intersections
            int S = scanner.nextInt(); // Index of the intersection where Charles parked his car
            int T = scanner.nextInt(); // Index of the intersection where Charles agreed to meet Bernard
    
            // Create graph and initialize distances
            int[][] graph = new int[N][N];
            for (int i = 0; i &lt; N; i++) {
                for (int j = 0; j &lt; N; j++) {
                    graph[i][j] = scanner.nextInt();
                }
            }
    
            int shortestPath = dijkstra(N, S, T, graph);
            System.out.println(shortestPath);
    
            scanner.close();
        }
    
        private static int dijkstra(int N, int S, int T, int[][] graph) {
            int[] dist = new int[N];
            boolean[] visited = new boolean[N];
            Arrays.fill(dist, Integer.MAX_VALUE);
            dist[S] = 0;
    
            for (int i = 0; i &lt; N - 1; i++) {
                int u = findMinDistance(dist, visited);
                visited[u] = true;
    
                for (int v = 0; v &lt; N; v++) {
                    if (!visited[v] &amp;&amp; graph[u][v] &gt; 0 &amp;&amp; dist[u] != Integer.MAX_VALUE &amp;&amp; dist[u] + graph[u][v] &lt; dist[v]) {
                        dist[v] = dist[u] + graph[u][v];
                    }
                }
            }
    
            return dist[T];
        }
    
        private static int findMinDistance(int[] dist, boolean[] visited) {
            int minDist = Integer.MAX_VALUE;
            int minIndex = -1;
    
            for (int i = 0; i &lt; dist.length; i++) {
                if (!visited[i] &amp;&amp; dist[i] &lt; minDist) {
                    minDist = dist[i];
                    minIndex = i;
                }
            }
    
            if (minIndex == -1) {
                throw new RuntimeException(&quot;No unvisited node with a lower distance found&quot;);
            }
    
            return minIndex;
        }
    }

