int maxDay = switch(month) {
    case 1,3,5,7,8,10,12 -&gt; 31; // months with 31 days
    case 4,6,9,11 -&gt; 30;        // months with 30 days
    case 2 -&gt; {                 // special case for February
                if ((year % 4 == 0 &amp;&amp; year % 100 != 0) || year % 400 == 0) {
                     yield 29;  // is leap year
                } 
                yield 28;
    }
    default -&gt; -1;
};
if (day &lt; 1 || day &gt; maxDay) {
    System.out.printf(&quot;Day(%d) is out of range for month(%d).&quot;, day, month);
}
```
