    public class Mongo[n]Configuration {
    
    	@Autowired
    	private MongoConfigurationProperties mongoProperties;
    
    	@Bean(name = &quot;Mongo[n]Template&quot;)
    	public MongoTemplate mongo[n]Template() throws Exception {
    		return new MongoTemplate(metadataFactory(this.mongoProperties.getMongo[n]()));
    	}
     
    	@Bean
