    import java.io.IOException;
    
    @Component
    public class SystemIsReadyFilter implements Filter {
    
    	@Autowired
    	private ApplicationManager applicationManager;
    
    	@Override
    	public void init(FilterConfig filterConfig) throws ServletException {}
    
    	@Override
    	public void doFilter(ServletRequest request, ServletResponse response, FilterChain chain) throws IOException, ServletException {
    		if (!applicationManager.isApplicationReady()) {
    			((HttpServletResponse) response).sendError(HttpServletResponse.SC_SERVICE_UNAVAILABLE, &quot;The service is booting.&quot;);
    		} else {
    			chain.doFilter(request, response);
    		}
    	}
    
    	@Override
    	public void destroy() {}
    }

