            return true;
        }
        else if(column == size){
            return solve(board, 1, row + 1, size);
        }
        else if(board[row][column] != 0){
            return solve(board, column + 1, row, size);
        }
        else{
            for(int k=1; k&lt;10; k++){
                if(isValid(board, column, row, k)){
                    board[row][column] = k;
                    if(solve(board, column + 1, row, size)){
