          public void method1(){
        
          }
    
     public void method3(){
        
          }
    
    
        }
        
        public class SubClass extends SuperClass {
          public void method2()
          {
        
          }
    @override
     public void method3(){
        
          }
        }
        
        SubClass sub = new SubClass();
        sub.method1();  //Valid through inheritance from SuperClass
        sub.method2();  // Valid 
        sub.method3();//valid 

    
       superClass sc1 = new subClass();
