            public TestPane() {
                addMouseMotionListener(new MouseAdapter() {
                    @Override
                    public void mouseMoved(MouseEvent e) {
                        highlighted = null;
                        for (Shape cell : cells) {
                            if (cell.contains(e.getPoint())) {
                                highlighted = cell;
