                    g2d.fill(highlighted);
                }
                g2d.setColor(Color.BLACK);
                for (Shape cell : cells) {
                    g2d.draw(cell);
                }
                g2d.dispose();
            }
    
            @Override
            public Dimension getPreferredSize() {
                return new Dimension(200, 200);
            }
    
        }
    }

