        Preconditions.checkNotNull(serviceInterface, &quot;SPI interface is null.&quot;);
        Preconditions.checkArgument(serviceInterface.isInterface(), &quot;SPI interface `%s` is not interface.&quot;, serviceInterface);
    }
    
