    class Solution {
        public List&lt;List&lt;Integer&gt;&gt; subsets(int[] nums) {
            List&lt;List&lt;Integer&gt;&gt; ans = new ArrayList&lt;&gt;();
            
            generateSubsets(0, ans,new ArrayList&lt;&gt;(), nums);
            return ans;
        }
        public void generateSubsets(int i, List&lt;List&lt;Integer&gt;&gt; ans, List&lt;Integer&gt; subset, int[] nums ){
            if( i == nums.length){
                ans.add(subset); // adds all empty subsets in the ans
                return;
            }
            subset.add(nums[i]);
            generateSubsets(i+1, ans, subset, nums);
            subset.remove(subset.size() -1);
            generateSubsets(i+1, ans, subset, nums);
