    JSONArrayIterator(JSONArray jsonArray) {
        this.jsonArray = jsonArray;
        this.l = jsonArray.length();
        this.i = 0;
    }
    @SuppressWarnings(&quot;unchecked&quot;)
    public E next() {
        try {
            Object o = this.jsonArray.get(this.i++);
            return (E)o;
        } catch (JSONException ex) {
        }
        return null;
    }

}
```

