  public void moveAnt(ArrayList&lt;Ant&gt; ants, AntMound antHill) {
    int antHillX = antHill.getX();
    int antHillY = antHill.getY();
    //System.out.println(&quot;HILL DIST: &quot; + distanceToAnt(antHillX, antHillY));
    if (getNearestAnt(ants) != null &amp;&amp;  distanceToAnt(getNearestAnt(ants).getX(), getNearestAnt(ants).getY()) &lt; 100) {
      // Move towards the nearest ant
      Ant nearestAnt = getNearestAnt(ants);
      int nearestAntX = nearestAnt.getX();
      int nearestAntY = nearestAnt.getY();
      moveTowards(nearestAntX, nearestAntY, ants);
    }
    else if (distanceToAnt(antHillX, antHillY) &lt; 150){
      moveTowards(antHillX, antHillY, ants);
      rX = antHillX;
      rY = antHillY;
      if(distanceToAnt(antHillX, antHillY) &lt;= 10){
        antHill.takeDamge();
      }
    }
    else{
      //RANDOM MOVEMENT
      if(distanceToAnt(rX,rY) &lt; 10){
        atTarget = true;
        //System.out.println(&quot;at target&quot;);
      }
      if(atTarget){
        int nrX = (int) Math.floor(Math.random() *(WIDTH - 0 + 1) + 0);
        rY = (int) Math.floor(Math.random() *(HEIGHT - 0 + 1) + 0);
        if(player.equals(&quot;p1&quot;) &amp;&amp; rX != 0){
          while(nrX &lt; rX){
            nrX = (int) Math.floor(Math.random() *(WIDTH - 0 + 1) + 0);
          }
        }
        else if(rX != 0){
          while(nrX &gt; rX){
            nrX = (int) Math.floor(Math.random() *(WIDTH - 0 + 1) + 0);
          }
        }
        rX = nrX;
        atTarget = false;
        //System.out.println(&quot;target is X:&quot; + rX + &quot; Y:&quot; + rY);
      }
      
      moveTowards(rX, rY, ants);
    } 
    
  }

  private int distanceToAnt(int x, int y) {
    int dx = x - this.x;
    int dy = y - this.y;
    return (int) Math.sqrt(dx * dx + dy * dy);
  }

  private Ant getNearestAnt(ArrayList&lt;Ant&gt; ants) {
    Ant nearestAnt = null;
    double minDistance = Double.MAX_VALUE;

    for (Ant ant : ants) {
      int distance = distanceToAnt(ant.getX(), ant.getY());
      if (distance &lt; minDistance) {
        minDistance = distance;
        nearestAnt = ant;
      }
    }

    return nearestAnt;
  }

  private void moveTowards(int targetX, int targetY, ArrayList&lt;Ant&gt; ants) {
    int dx = targetX - x;
    int dy = targetY - y;
    int distance = (int) Math.sqrt(dx * dx + dy * dy);

    if (distance &gt; 0) {
      int nspeed = (int) speed/3;
      int moveX = (int) Math.round((double) dx / distance * nspeed);
      int moveY = (int) Math.round((double) dy / distance * nspeed);

      if (!isCollidingWithOtherAnt(ants)) {
        x += moveX;
        y += moveY;
      }
      else{
        //collision
        attackAnt(getNearestAnt(ants));
      }
    }
  }

  private boolean isCollidingWithOtherAnt(ArrayList&lt;Ant&gt; ants) {
    for (Ant ant : ants) {
      if (ant != this &amp;&amp; isColliding(ant)) {
        return true;
      }
    }
    return false;
  }

  private boolean isColliding(Ant otherAnt) {
    int radius = 10; // Adjust the radius as needed for collision detection
    int dx = x - otherAnt.getX();
    int dy = y - otherAnt.getY();
    int distanceSquared = dx * dx + dy * dy;
    int minDistanceSquared = radius * radius * 4; // Adjust the factor (4) as needed for collision detection

    return distanceSquared &lt; minDistanceSquared;
  }

