public static void pyramid_Left(int steps, boolean useSaperator, boolean isTriangle) {
	String saperator = &quot; * &quot;;
	for (int i = 1; i &lt;= steps; i++) {
		// Forward
		for (int j = 1; j &lt;= i; j++) {
			print(i);
			if (useSaperator &amp;&amp; j+1 &lt;= i) {
				print(saperator);
			}
		}
		print(&quot;\n&quot;);
		if (i == steps &amp;&amp; !isTriangle) {
			// Backward - After completing Forward
			int k = i - 1;
			while (k &gt; 0) {
				for (int j = k; j &gt; 0; j--) {
					print(k);
					if (useSaperator &amp;&amp; j-1 &gt; 0) {
						print(saperator);
					}
				}
				k--;
				print(&quot;\n&quot;);
			}
		}
	}
}
