          MQQueueManager qMgr = null;
          MQQueue        destQueue = null;
          MQMessage      tmpMsg = new MQMessage();
          try {
              qMgr = new MQQueueManager(&quot;QM1&quot;, mqht);
              destQueue = qMgr.accessQueue(&quot;TESTQ&quot;, openOptions);
    
              tmpMsg.write(&quot;Msg1&quot;.getBytes());
