    public static void main(final String... args) {

        getDefaultConsoleHandler().ifPresentOrElse(
                consoleHandler -&gt; consoleHandler.setFormatter(getMinimalFormatter()),
                () -&gt; System.err.println(&quot;Could not get default ConsoleHandler&quot;));

