    class SpotWithObservedDates {
       @Embedded
       Spot spot;
       @Relation(
               entity = ObservedDate.class,
               parentColumn = &quot;id&quot;,
               entityColumn = &quot;parentSpotId&quot;
       )
       List&lt;ObservedDate&gt; observedDateList;
    }

