@Service
public class JwtService {

    @Value(&quot;${jwt.secret-key}&quot;)
    private String SECRET_KEY;

    @Value(&quot;${jwt.expiration}&quot;)
    private long jwtEXPIRATION;

    @Value(&quot;${jwt.refresh-token.expiration}&quot;)
    private long jwtREFRESH_EXPIRATION;

    public String extractLogin(String token) {
        return extractClaim(token, Claims::getSubject);
    }

    public &lt;T&gt; T extractClaim(String token, Function&lt;Claims, T&gt; claimsResolver) {
        final Claims claims = extractAllClaims(token);
        return claimsResolver.apply(claims);
    }

    public String extractTokenFromRequest(HttpServletRequest request) {
        final String authorizationHeader = request.getHeader(&quot;Authorization&quot;);
        if (authorizationHeader != null &amp;&amp; authorizationHeader.startsWith(&quot;Bearer &quot;)) {
            return authorizationHeader.substring(7);
        }
        return null;
    }

    public String generateToken(UserDetails userDetails) {
        return generateToken(Map.of(), userDetails);
    }

    public String generateRefreshToken(UserDetails userDetails) {
        Map&lt;String, Object&gt; extraClaims = new HashMap&lt;&gt;();
        return buildToken(extraClaims, userDetails, jwtREFRESH_EXPIRATION);
    }

    public String generateToken(Map&lt;String, Object&gt; extraClaims, UserDetails userDetails) {
            return buildToken(extraClaims, userDetails, jwtEXPIRATION);
    }

    private String buildToken(Map&lt;String, Object&gt; extraClaims, UserDetails userDetails, long expiration) {
        final Date now = new Date();
        final Date validity = new Date(now.getTime() + expiration);
        return Jwts.builder()
                .setClaims(extraClaims)
                .setSubject(userDetails.getUsername())
                .setIssuedAt(now)
                .setExpiration(validity)
                .signWith(getSignInKey(), SignatureAlgorithm.HS256)
                .compact();
    }

    public boolean isTokenValid(String token, UserDetails userDetails) {
        final String login = extractLogin(token);
        return (login.equals(userDetails.getUsername()) &amp;&amp; !isTokenExpired(token));
    }

    private boolean isTokenExpired(String token) {
        return extractExpiration(token).before(new Date());
    }

    private Date extractExpiration(String token) {
        return extractClaim(token, Claims::getExpiration); // Claims::getExpiration is a method reference
    }


    private Claims extractAllClaims(String token) {
        System.out.println(&quot;Token exctact : &quot; + token);
        return Jwts.parserBuilder().setSigningKey(getSignInKey())
                .build()
                .parseClaimsJws(token)
                .getBody();
    }

    private Key getSignInKey() {
        byte keyBytes[] = Decoders.BASE64.decode(SECRET_KEY);
        return Keys.hmacShaKeyFor(keyBytes);
    }
}
