    fun Array&lt;Array&lt;Int&gt;&gt;.matrixSum(b: Array&lt;Array&lt;Int&gt;&gt;): Array&lt;Array&lt;Int&gt;&gt; {
      return mapIndexed { indexA, valueA -&gt;
        valueA.mapIndexed { indexB, valueB -&gt;
