            break;
        } else if (age &lt;= 0) {
            JOptionPane.showMessageDialog(null, &quot;Invalid response!&quot;);
        }
    } catch (NumberFormatException exception) {
        JOptionPane.showMessageDialog(null, &quot;Invalid age&quot;);
    }
}
```

