    public static boolean is_pdf(byte[] data) {
        if (data != null &amp;&amp; data.length &gt; 4 &amp;&amp;
                data[0] == 0x25 &amp;&amp; // %
                data[1] == 0x50 &amp;&amp; // P
                data[2] == 0x44 &amp;&amp; // D
                data[3] == 0x46 &amp;&amp; // F
                data[4] == 0x2D) { // -

            // version 1.3 file terminator
            if (data[5] == 0x31 &amp;&amp; data[6] == 0x2E &amp;&amp; data[7] == 0x33 &amp;&amp;
                    data[data.length - 7] == 0x25 &amp;&amp; // %
                    data[data.length - 6] == 0x25 &amp;&amp; // %
