            else if(n_disk&gt;1) {
                solve(n_disk - 1, src_bolt, auxiliary, dest_bolt);
                disk_mover(src_bolt, dest_bolt);
                solve(n_disk-1, auxiliary, dest_bolt, src_bolt);
