        super.onNewIntent(intent);
        handleNfcIntent(intent);
    }

    private void handleNfcIntent(Intent intent){
        String action= intent.getAction();
        if (NfcAdapter.ACTION_NDEF_DISCOVERED.equals(action) ||
                NfcAdapter.ACTION_TECH_DISCOVERED.equals(action)) {
            Tag tag = intent.getParcelableExtra(NfcAdapter.EXTRA_TAG);
            if (tag != null) {
                Ndef ndef = Ndef.get(tag);
                if (ndef != null) {
                    try {
                        ndef.connect();
                        NdefMessage ndefMessage = ndef.getNdefMessage();
                        if (ndefMessage != null) {
                            NdefRecord[] records = ndefMessage.getRecords();
                            for (NdefRecord record : records) {
                                String payload = readTextFromNdefRecord(record);
                                // Do something with the payload
                                Toast.makeText(this, &quot;NFC Tag Content: &quot; + payload, Toast.LENGTH_SHORT).show();
                            }
                        }
                        ndef.close();
                    } catch (Exception e) {
                        Log.e(TAG, &quot;Error reading NFC tag&quot;, e);
                    }
                } else {
                    Toast.makeText(this, &quot;NDEF is not supported by this NFC tag&quot;, Toast.LENGTH_SHORT).show();
                }
            }
        }
    }

    private String readTextFromNdefRecord(NdefRecord record) throws UnsupportedEncodingException{
        byte[] payload= record.getPayload();

        String textEncoding=((payload[0] &amp; 0*80) ==0)? &quot;UTF-8&quot;: &quot;utf-16&quot; ;

        //get language code
        int languageCodeLength = payload[0] &amp; 0x3F;
        String languageCode= new String(payload, 1, languageCodeLength, StandardCharsets.US_ASCII);

        //get payload
        String textPayload= new String(payload, 1+ languageCodeLength, payload.length - 1 - languageCodeLength, textEncoding);

        return textPayload;
    }
