    mvn deploy:deploy-file \
      -DgroupId=org.example \
      -DartifactId=mylib\
      -Dversion=1.1 \
      -Dfile=/path/to/mylib-1.1-javadoc.jar \
      -Dpackaging=jar \
      -Durl=file://path/to/your-project/fake-repo \
      -Dclassifier=javadoc

