		for(String part : address.split(Pattern.quote(&quot;.&quot;))) {
			// shift the previously parsed bits over by 1 byte
			result = result &lt;&lt; 8;
			// set the low order bits to the current octet
			result |= Integer.parseInt(part);
		}
		return result;
	}

