        {
            &quot;name&quot; : &quot;San Francisco&quot;,
            &quot;number&quot; : &quot;298732&quot;,
            &quot;center&quot; : {
                    &quot;latitude&quot; : 37.783333, 
                    &quot;longitude&quot; : -122.416667
                }
        }
