  Hint getHint(
      Map&lt;…, …&gt; numberMap,
      Board penBoard,
      Board pencilBoard,
      Board solvedBoard,
      Collection&lt;Hint&gt; hints)
}

class NakedSingle implements HintProvider {
  @Override
  public Hint getHint(
      final Map&lt;…, …&gt; numberMap,
      final Board penBoard,
      final Board pencilBoard,
      final Board solvedBoard,
      final Collection&lt;Hint&gt; hints) {
    // impl …
  }
}
