```java
int[][] matrix(String string) throws IOException {
    BufferedReader reader = new BufferedReader(new StringReader(string));
    int count = 0;
    String line;
    while (reader.readLine() != null)
        count++;
    reader = new BufferedReader(new StringReader(string));
