        @Override
        public void log(String message) {
    
            // Blacklist the elements not required
            for (String pattern: REQUEST_PATTERNS) {
                if (message.startsWith(pattern)) {
                    return;
                }
            }
            // Any response patterns as well...
            for (String pattern: RESPONSE_PATTERNS) {
                if (message.startsWith(pattern)) {
                    return;
                }
            }
