    public User findByUsername(String username) {
        username = username.toLowerCase();
        List&lt;User&gt; user;
        EntityManager entityManager = hibernateService.getEntityManagerFactory().createEntityManager();
        CriteriaBuilder criteriaBuilder = hibernateService.getEntityManagerFactory().getCriteriaBuilder();
        CriteriaQuery&lt;User&gt; criteriaQuery = criteriaBuilder.createQuery(User.class);
        Root&lt;User&gt; root = criteriaQuery.from(User.class);
        CriteriaQuery&lt;User&gt; all = criteriaQuery.select(root);
        all.where(criteriaBuilder.equal(root.get(&quot;username&quot;), username));
        TypedQuery&lt;User&gt; allQuery = entityManager.createQuery(all);
        user = allQuery.getResultList();
        return (user == null || user.size() == 0) ? null : user.get(0);
    }
}


