	public static &lt;T, K, U&gt; Collector&lt;T, ?, Map&lt;K, U&gt;&gt; toMapNullFriendly(
			Function&lt;? super T, ? extends K&gt; keyMapper,
			Function&lt;? super T, ? extends U&gt; valueMapper) {
		@SuppressWarnings(&quot;unchecked&quot;)
		U none = (U) new Object();
