    def count(low, high, zeros, ones):
      def count_k(k):
        if k&lt;0:
          return 0
        if k not in cache:
          cache[k] = count_k(k-zeros) + count_k(k-ones)
        return cache[k]
    
      cache = {0:1}
      return sum(count_k(k) for k in range(low, high+1))

