    public static int getDigitCount(BigInteger number) {
      double factor = Math.log(2) / Math.log(10);
      int digitCount = (int) (factor * number.bitLength() + 1);
      if (BigInteger.TEN.pow(digitCount - 1).compareTo(number) &gt; 0) {
        return digitCount - 1;
      }
      return digitCount;
    }

