int findSubArray(int arr[], int n) {
    if(n&lt;=0){
        return 0;
    }
    int maxLen = 1;
    int prevPos = -1;
    for(int i=1;i&lt;n;i++){
        maxLen = max(maxLen, i-1-prevPos);
        if(arr[i-1] == arr[i]){
            prevPos = i-1;
        }
    }
    maxLen = max(maxLen, n-1 - prevPos);
    return maxLen;
}

