    import java.util.ArrayList;
    import java.util.List;
    import java.util.Scanner;
    
    /**
     * Replace repeating elements with greater that greatest values
     * Input : arr = {1, 3, 4, 5, 3}
     * Output : 1 3 4 5 6
     * Explanation: Here 3 is repeating so it is replaced with 6
     */
    public class ReplaceRepeatedElementWithGreaterValue {
        //returns the maximum element
        static int max_element(int arr[]) {
            int max = arr[0];
            for (int i = 1; i &lt; arr.length; i++) {
                if (max &lt; arr[i])
                    max = arr[i];
            }
            return max;
        }
    
        static void replaceElements(int arr[], int n) {
            // Maximum element in an array
            int maxx = max_element(arr);
            List&lt;Integer&gt; s = new ArrayList&lt;&gt;();
            for (int i = 0; i &lt; n; i++) {
                // check whether the element is
                // repeated or not
                if (!s.contains(arr[i])) {
                    s.add(arr[i]);
                } else {
                    // update the repeated element with the
                    // maxx element
                    arr[i] = maxx + 1;
                    maxx++; // update the max
                    // mark the maximum element as visited
                    s.add(maxx);
                }
            }
        }
    
        public static void main(String[] args) {
            Scanner sc = new Scanner(System.in);
            System.out.println(&quot;enter the size of the input array:&quot;);
            int n = sc.nextInt();
            int[] arr = new int[n];
            System.out.println(&quot;enter the elements of an array:&quot;);
            for (int i = 0; i &lt; n; i++) {
                arr[i] = sc.nextInt();
            }
            replaceElements(arr, n);
            for (int i = 0; i &lt; n; i++) {
                System.out.print(arr[i] + &quot; &quot;);
            }
        }
    }

