    int val;
    DoublyListNode next;
    DoublyListNode prev;

    public DoublyListNode(int val) {
        this.val = val;
        this.next = null;
        this.prev = null;
    } 
}

class MyLinkedList {    

    DoublyListNode head;
    DoublyListNode tail;
    
    public MyLinkedList() {
       head = new DoublyListNode(-1);
       tail = new DoublyListNode(-1);

       head.next = tail;
       tail.prev = head;
    }
    
    public int get(int index) {
        DoublyListNode curr = head;
        int i = 0;

        while(tail != null &amp;&amp; index &gt; 0){
            curr = curr.next;
            index--;     
        }

        if(index == 0 &amp;&amp; curr != null){
            return curr.val;
        } 
        return -1;
    }
    
    public void addAtHead(int val) {
        DoublyListNode newHead = new DoublyListNode(val);
        head.next.prev = newHead;
        head.next = newHead;
        newHead.next = head.next;
        newHead.prev = head;
    }
    
    public void addAtTail(int val) {
        DoublyListNode newTail = new DoublyListNode(val);

        tail.prev.next = newTail;
        tail.prev = newTail;
        newTail.next = tail;
        newTail.prev = tail.prev;
    }
    
    public void addAtIndex(int index, int val) {
        DoublyListNode curr = head;

        while(tail != null &amp;&amp; index &gt; 0){
            curr = curr.next;
            index--;
        }

        if(index == 0 &amp;&amp; curr != null){
            DoublyListNode newNode = new DoublyListNode(val);
            newNode.prev = curr.prev;
            newNode.next = curr.next;
            curr.prev.next = newNode;
            curr.next.prev = newNode;
        }
    }
    
    public void deleteAtIndex(int index) {            
        DoublyListNode curr = head;

        while(tail != null &amp;&amp; index &gt; 0){
            curr = curr.next;
            index--;
        }
        if(index == 0 &amp;&amp; curr != null &amp;&amp; curr.next != null &amp;&amp; curr.prev != null){
            curr.prev.next = curr.next;
            curr.next.prev = curr.prev;
        }
    }
}    

