class Result {
    public static int activityNotifications(List&lt;Integer&gt; expenditure, int d) {
        int notices = 0;
        int len = expenditure.size();
        for(int i = d; i &lt; len; i++){
            int[] clone = new int[d];

            for(int j = 0; j &lt; d; j++){
                clone[j] = expenditure.get(i - d + j);
            }

            MergeSort.mergeSort(clone, 0, d - 1);
            double median;
            if (d % 2 == 0){
                median = (clone[d/2] + clone[d/2 + 1]) / 2.0;
            } else{
                median = clone[d / 2];
            }
            if(expenditure.get(i) &gt;= median * 2){
                notices++;
            }
        }
        return notices;
    }
}

class MergeSort{
    public static void mergeSort(int[] arr, int l, int h){
        if (l &lt; h){
            int mid  = (l + h) / 2;
            mergeSort(arr, l ,mid);
            mergeSort(arr, mid + 1 , h);
            merge(arr, l, mid, h);
        }
    }

    public static void merge(int[] arr, int l, int mid, int h){
        int n1 = mid - l + 1;
        int n2 = h - mid;

        int[] L = new int[n1];
        int[] R = new int[n2];

        for (int p = 0; p &lt; n1; p++){
            L[p] = arr[p + l];
        }

        for (int p = 0; p &lt; n2; p++){
            R[p] = arr[p + mid + 1];
        }

        int k = l;
        int i = 0;
        int j = 0;

        while (i &lt; n1 &amp;&amp; j &lt; n2){
            if (arr[i] &lt;= arr[j]){
                arr[k] = L[i];
                i++;
                k++;
            }
            else {
                arr[k] = R[j];
                j++;
                k++;
            }

            while(i &lt; n1){
                arr[k] = L[i];
                i++;
                k++;
            }

            while(j &lt; n2){
                arr[k] = R[j];
                j++;
                k++;
            }
        }
    }  
}


