	{
		for (int i = 0; i &lt; toolbar.getChildCount(); i++) {
			if (toolbar.getChildAt(i) instanceof AppCompatTextView) {
				UIUtil.setFont(font, (AppCompatTextView) toolbar.getChildAt(i));
