public class MultiThreadCopier {
    public static final long SAFE_MARGIN = 6;
    Worker[] workers;

    public MultiThreadCopier(SourceProvider sourceProvider, String dest, int workerCount) {
        workers = new Worker[workerCount];
        long interval = sourceProvider.size() / workerCount;
        for (int i = 0; i &lt; workerCount; i++) {
            long start = i * interval;
            workers[i] = new Worker(dest, start, interval + ((i + 1 == workerCount) ? sourceProvider.size() % workerCount : 0), sourceProvider, this);
        }
    }

    public void start() {
        for (Worker worker : workers) {
            worker.start();
        }
    }


    public Pair&lt;Worker, Long&gt; busiestWorker() {
        long mostWorkRemaining = 0;
        Worker busiestWorker = null;
        for (Worker worker : workers) {
            if (worker.isDone)
                continue;
            long remainingWork = worker.end - worker.cursor + 1;
            if (remainingWork &lt; SAFE_MARGIN)
                continue;
            if (remainingWork &lt;= mostWorkRemaining)
                continue;
            mostWorkRemaining = remainingWork;
            busiestWorker = worker;
        }
        if (busiestWorker == null)
            return new Pair&lt;&gt;(null, 0L);
        busiestWorker.paused = true;
        return new Pair&lt;&gt;(busiestWorker, mostWorkRemaining);
    }
}
```

